firestore.rules:
service cloud.firestore {

  match /databases/{database}/documents {

    // user_actions: Users can only create/read their own actions, can only update matchStatus
    match /user_actions/{actionId} {
      allow create: if request.auth != null && request.resource.data.actorUserId == request.auth.uid;
      allow read: if request.auth != null && (resource.data.actorUserId == request.auth.uid || resource.data.targetUserId == request.auth.uid);
      allow update: if request.auth != null &&
        resource.data.actorUserId == request.auth.uid &&
        request.resource.data.keys().hasOnly(['matchStatus']) &&
        request.resource.data.matchStatus is bool;
      allow delete: if false;
    }

    // matches: Users can only read matches they're part of, no direct writes
    match /matches/{matchId} {
      allow read: if request.auth != null && (resource.data.userA == request.auth.uid || resource.data.userB == request.auth.uid);
      allow write: if false; // Only allow via cloud functions
    }

    // Other rules ...
  }
}
